
string input = "";

int found = 0;
int lineCounter = 0;
char[] temporary = {'s','g','r','r'};

while (found == 0)
{
    if ((temporary[0] != temporary[1]) && (temporary[0] != temporary[2]) && (temporary[0] != temporary[3]) && (temporary[1] != temporary[2]) && (temporary[1] != temporary[3]) && (temporary[2] != temporary[3]))
    {
        found = 1;
    }

    temporary[3] = temporary[2];
    temporary[2] = temporary[1];
    temporary[1] = temporary[0];
    temporary[0] = input[lineCounter];

    Console.WriteLine(lineCounter + 4);

    lineCounter++;
}
